		// repeat this segment for each datasource to filter
		var requestQueryParameterCollection = generator.RequestQueryParameterCollection;
		requestQueryParameterCollection.AddRequestQueryParameter(
																'PurchTable',
																'PurchId', currentPurchTable.PurchId
																);
		System.Uri fullURI = generator.GenerateFullUrl();
		browser.navigate(fullURI.AbsoluteUri, true);
	}​
}​

/// <summary>
/// Gets infolog content
/// </summary>
/// <returns>infolog string</returns>
public static str getErrorStr(container _cont)
{
	SysInfologEnumerator        enumerator;
	SysInfologMessageStruct     msgStruct;
	Exception                   exception;
	str                         error;

	enumerator = SysInfologEnumerator::newData(_cont);

	while (enumerator.moveNext())
	{
		msgStruct = new SysInfologMessageStruct(enumerator.currentMessage());
		exception = enumerator.currentException();

		error = strfmt('%1 %2', error, msgStruct.message());
	}

	return error;
}

class UpdateDuedate_CustAging
{
    public static void main(Args _args)
    {
        CustTrans       CustTrans;
        CustInvoiceJour         CustInvoiceJour;
        
        ttsBegin;
        while select forupdate CustInvoiceJour where CustInvoiceJour.payment =='Test'
            && CustInvoiceJour.DueDate == CustInvoiceJour.InvoiceDate  
          //  && CustTrans.Invoice=='INV-0000001623'


        {
            CustInvoiceJour.DueDate = PaymTerm::find(CustInvoiceJour.payment).due(CustInvoiceJour.InvoiceDate);
            CustInvoiceJour.update();
        }
        ttsCommit;


    }


}

